import repository from './shortUrl.repository';
import schema from './shortUrl.validation';
import { errors, errorRedirect } from '../../utils';

const { applicationError } = errors;

class ShortUrlController {
  async create(req, res, next) {
    try {
      const { redirectUrl, expirationDateMs } = req.body;

      const errorSchema = schema.getErrorsCreateParams({ redirectUrl, expirationDateMs });

      if (errorSchema) {
        applicationError({ message: errorSchema, status: 400 });
      }

      const newUrl = await repository.create({ redirectUrl, expirationDateMs });

      res.status(200).json({ shortUrl: `${process.env.HOST_URL}/${newUrl._id}` });
    } catch (err) {
      next(err);
    }
  }

  async redirect(req, res, next) {
    try {
      const { id } = req.params;

      if (!schema.isRedirectParamsValid(id)) {
        errorRedirect.deliverExpirationUrlPage(res);
        return;
      }

      const url = await repository.findOne(id);

      const isUrlValid = schema.isRedirectUrlValid(url);

      if (!isUrlValid) {
        errorRedirect.deliverExpirationUrlPage(res);
        return;
      }

      res.status(301).redirect(url.redirectUrl);
    } catch (err) {
      next(err);
    }
  }
}

export default new ShortUrlController();

