import Url from '../../models/Url';

class ShortUrlRepository {
  async create(newUrlParams) {
    try {
      const newUrl = await Url.create(newUrlParams);

      return newUrl;
    } catch (error) {
      console.log(error);
    }
  }

  async findOne(urlId) {
    try {
      const oneUrl = await Url.findOne({ _id: urlId });

      return oneUrl;
    } catch (error) {
      console.log(error);
    }
  }
}

export default new ShortUrlRepository();

