      status: err.status || 500
    };
  }

  handleError(err, req, res, next) {
    const {
      status,
      message
    } = this.catchErrorType(err);
    res.status(status || '').send({
      message,
      status
    });
  }

  applicationError(params) {
    throw new ApplicationError(params);
  }

}

module.exports = new ErrorHandler();
